



$(document).ready(function() {
    // Handle the change in ticket quantity
    $('#ticketQuantity').on('input', function() {
        var pricePerTicket = parseInt($('#ticketPrice').text());
        var totalCost = $(this).val() * pricePerTicket;
        $('#totalCost').text(totalCost);
    });

    // Handle form submission
    $('#ticketForm').on('submit', function(e) {
        e.preventDefault();
        var numberOfTickets = $('#ticketQuantity').val();
        // Process the checkout here (e.g., AJAX request or redirect)
        console.log(`Number of tickets: ${numberOfTickets}`);
        // Close the modal after processing
        $('#ticketModal').modal('hide');

        // Implement any additional logic for ticket selection here
        // This might involve AJAX requests to your server, updates to the UI, etc.
        
        // Example alert (you can remove this once real functionality is implemented)
        alert(`Checkout for ${numberOfTickets} tickets`);
    });
});
